drop database if exists library;
create database library char set utf8;
use library;

create table book(
	id int not null primary key auto_increment,
	book_name varchar(120) not null,
    description text,
    publication_date date not null default '2018-01-01',
    category varchar(30) not null,
    isbn varchar(20) not null unique,
    price decimal(5,2) not null default 0.00,
    pages smallint not null,
    author_first_name varchar(45) not null,
    author_last_name varchar(45) not null,
    author_email varchar(50) not null unique,
    author_age tinyint not null,
    author_date_of_birth date not null,
    author_awards varchar(120)
);

