drop database if exists social_network;
create database social_network char set utf8;
use social_network;

create table users(
	id int not null primary key auto_increment,
    username varchar(120) not null unique,
    password varchar(40) not null,
    email varchar(50) not null unique,
    created_at timestamp default now(),
    is_deleted boolean default false
);

create table messages (
	user_sender_id int not null,
    user_receiver_id int not null,
    text varchar(1024) not null,
    created_at timestamp default now(),
    primary key(user_sender_id, user_receiver_id)
);

alter table messages add check (
	user_sender_id <> user_receiver_id
);

create table alboms(
	id int not null primary key auto_increment,
    name varchar(35) not null,
    qty_photos smallint default 0,
    created_at timestamp default now(),
    is_deleted boolean default false
);

create table photos(
	id int not null primary key auto_increment,
    image_url text not null,
    created_at timestamp default now(),
    is_deleted boolean default false
);

create table user_albom(
	user_id int not null,
	albom_id int not null,
    primary key(user_id, albom_id)
);

create table user_photo(
	user_id int not null,
	photo_id int not null,
    primary key(user_id, photo_id)
);

create table albom_photo(
	albom_id int not null,
	photo_id int not null,
    primary key(albom_id, photo_id)
);

alter table messages add foreign key(user_sender_id)
	references users(id);
alter table messages add foreign key(user_receiver_id)
	references users(id);
    
alter table user_albom add foreign key(user_id)
	references users(id);
alter table user_albom add foreign key(albom_id)
	references alboms(id);
    
alter table user_photo add foreign key(user_id)
	references users(id);
alter table user_photo add foreign key(photo_id)
	references photos(id);

alter table albom_photo add foreign key(albom_id)
	references alboms(id);
alter table albom_photo add foreign key(photo_id)
	references photos(id);
    
create index idx_username on users(username);
create index idx_email on users(email);
create index idx_text on messages(text);

